// Простая симуляция роста кедра с годами
class CedarTree {
  constructor() {
    this.age = 0;
    this.height = 0.1; // метры
    this.cones = 0;
  }

  growYear() {
    this.age++;
    // Молодые деревья растут быстрее
    if (this.age < 50) {
      this.height += 0.4 + Math.random() * 0.3;
    } else if (this.age < 100) {
      this.height += 0.2 + Math.random() * 0.2;
    } else {
      this.height += 0.05 + Math.random() * 0.1;
    }

    // Плодоношение начинается с ~25 лет
    if (this.age >= 25) {
      // Урожайные и неурожайные годы
      this.cones = this.age >= 25 ? Math.floor(Math.random() * 1000) : 0;
    }

    return {
      age: this.age,
      height: this.height.toFixed(2),
      cones: this.cones
    };
  }
}

// Пример использования
const tree = new CedarTree();
console.log("Рост кедра по годам:");
for (let year = 1; year <= 30; year++) {
  const stats = tree.growYear();
  if (year % 10 === 0) {
    console.log(`Возраст: ${stats.age} лет, Высота: ${stats.height} м, Шишек: ${stats.cones}`);
  }
}
