let bread = "Ломтик хлеба";
console.log("Берём: " + bread);
bread = "Хлеб с маслом";
console.log("Намазываем масло. Теперь у нас: " + bread);
bread = "Бутерброд с колбасой";
console.log("Кладём колбасу. Получился: " + bread);

let loveCheese = true; // попробуй поменять на false
let sandwich = "Бутерброд с колбасой";

if (loveCheese === true) {
  sandwich = sandwich + " и сыром";
  console.log("Добавляем сыр!");
} else {
  console.log("Сыр не добавляем.");
}

console.log("Итог: " + sandwich);

let biteCount = 1; // Начинаем с первого укуса

// Цикл "while" - означает "ПОКА"
while (biteCount <= 3) {
  console.log("Откусили раз: " + biteCount);
  biteCount = biteCount + 1; // Увеличиваем счётчик
}

console.log("Бутерброд съеден!");

// Объявляем функцию "makeSandwich"
function makeSandwich() {
  console.log("Берём хлеб.");
  console.log("Намазываем масло.");
  console.log("Кладём колбасу.");
  console.log("Бутерброд готов!");
}

// Вызываем функцию когда нужно
makeSandwich(); // Выполнится весь код внутри
