// Поднятие (Hoisting)
console.log(myVar); // undefined
var myVar = 10;
console.log(myVar); // 10

// Отсутствие блочной области видимости
if (true) {
    var x = 10;
}
console.log(x); // 10 - доступна вне блока!

// Блочная область видимости
if (true) {
    let y = 20;
}
console.log(y); // Ошибка!

// Временная мертвая зона
console.log(myLet); // Ошибка!
let myLet = 15;

// const с объектами
const person = { name: "John" };
person.name = "Alice"; // Можно!
person = { name: "Bob" }; // Ошибка!

