let d = 10;
let s = 1000;
let r = 0.1;

function calc(p, q, r) {
  let res = q;
  for (let i = 0; i < p; i++) {
    res += res * r;
  }
  return res;
}

right_column.content:
const daysOverdue = 10;
const debtAmount = 1000;
const penaltyRatePerDay = 0.1;

function calculateDebtWithPenalties(days, initialAmount, rate) {
  let currentAmount = initialAmount;
  for (let day = 0; day < days; day++) {
    currentAmount += currentAmount * rate;
  }
  return currentAmount;
}

// Глобальная область
const globalVar = 'Я глобальная!';

function myFunction() {
    // Функциональная область
    const functionVar = 'Я локальная!';

    if (true) {
        // Блочная область
        let blockVar = 'Я в блоке!';
        console.log(functionVar); // Доступно
    }

    console.log(blockVar); // Ошибка!
}

